    @accent-color: @catppuccin[@@lookup][@@accent];

    @white: if(@lookup = latte, @base, @text);
    @black: if(@lookup = latte, @text, @base);

    color: @text;
    background: @base !important;
    color-scheme: if(@lookup = latte, light, dark);

    ::selection {
