            }
            /// Default profile.
            /img[src *= '44884218_345707102882519_2446069589734326272'] {
                filter: invert(0.8) i;
            }
        }

        /// Login -> Links.
        ._2Lks6 { c: ac }

        /// Navbar -> Invert IG logo.
        if (ic_invert) {
            a[href='/'] > div > div  {
                > i[role='img'], img[src*='logo.png'] { filter: invert(0.8) }
